  // ];
  async function handleButtonSubmit(e){
    e.preventDefault()
    const response = await axios.get('https://crudcrud.com/api/d8eecc9113194f9cafaeda9696f88951')
    setMovies(response.data)

  }

  return (
    <React.Fragment>
      <section>
        <button onClick={()=>{handleButtonSubmit}}>Fetch Movies</button>
      </section>
      <section>
        <MoviesList movies={movies} />
      </section>
    </React.Fragment>
  );
}

export default App;
